import React from "react";
import { useState } from "react";
import { Motion, spring } from "react-motion";
import About from "./components/About.jsx";
import Contact from "./components/Contact.jsx";
import Navbar from "./components/Navbar.jsx";
import Portfolio from "./components/Portfolio.jsx";
import Skills from "./components/Skills.jsx";
import Welcome from "./components/Welcome.jsx";

const nav = ["Home", "About Me", "Skills", "Portfolio", "Contact"],
      skills = [
        { icon: "../assets/icons/javascript.png", name: "Javascript" },
        { icon: "../assets/icons/html.png", name: "HTML" },
        { icon: "../assets/icons/css.png", name: "CSS" },
        { icon: "../assets/icons/mongodb.png", name: "MongoDB" },
        { icon: "../assets/icons/react.png", name: "React" },
        { icon: "../assets/icons/node.png", name: "Node" },
        { icon: "../assets/icons/redux.png", name: "Redux" },
        { icon: "../assets/icons/d3.png", name: "D3" },
        { icon: "../assets/icons/bootstrap.png", name: "Bootstrap" },
        { icon: "../assets/icons/sass.png", name: "SASS" },
        { icon: "../assets/icons/git.png", name: "Git" },
        { icon: "../assets/icons/npm.png", name: "NPM" }
      ],
      replitLink = ".mateo-agl.repl.co",
      gitHubLink = "https://github.com/mateo-agl/",
      apps = [
        {repl: "messageboard", name: "Message Board"},
        {repl: "stockchecker", name: "Stock Checker"},
        {repl: "issuetracker", name: "Issue Tracker"},
        {repl: "library", name: "Library"},
        {repl: "metricimpconverter", name: "Metric/Imperial converter"},
      ],
      portfolio = apps.map(
        a => ({
          appLink: "https://" + a.repl + replitLink,
          codeLink: gitHubLink + a.repl,
          thumbnail: "../assets/thumbnails/" + a.name.toLowerCase().replace(/\W/g, "-") + "-thumbnail.png",
          name: a.name
        })
      ),
      links = [
        {link:"https://www.linkedin.com/in/mateo-aguilar-058b791a8/", icon:"bi bi-linkedin"},
        {link:"https://github.com/mateo-agl", icon:"bi bi-github"},
        {link:"https://www.freecodecamp.org/mateo-aguilar", icon:"fa fa-free-code-camp"},
        {link:"https://codepen.io/mateoagl", icon:"fa fa-codepen"}
      ]

const App = () => {
  const [data, setData] = useState({nav, portfolio, links});
  return (
    <Motion defaultStyle={{opacity: 0}} style={{opacity: spring(1)}}>
      {
        style =>
          <div className="text-center" style={style}>
          <Navbar data={data} setData={setData}/>
          <main className="container-fluid">
            <Welcome links={data.links}/>
            <About data={data} setData={setData}/>
            <Skills/>
            <Portfolio portfolio={data.portfolio}/>
            <Contact/>
          </main>
        </div>
      }
    </Motion>
  )
}

ReactDOM.render(<App/>, document.querySelector('#root'));
