import React from "react";
import { useState } from "react";
import { Motion, spring } from "react-motion";

export const Portfolio = props => {
    const [portState, setPortState] = useState({hover: false, key: ""});
    return (
      <section id="section-4" className="row mx-3 py-6">
        <h2 className="mt-2">My recent work</h2>
        <div className="col-lg-10 mx-auto border shadow projects">
            {
              props.portfolio.map(
                (p, i) => 
                <article 
                  className="row p-2 position-relative border-bottom" 
                  onMouseEnter={() => setPortState({hover: true, key: i})} 
                  onMouseLeave={() => setPortState({hover: false, key: ""})}
                  key={i}>
                  <Motion
                    defaultStyle={{opacity: 0.2}} 
                    style={{opacity: portState.hover && i === portState.key ? spring(0.85) : spring(0.2)}}>
                    {
                      style =>
                        <div className="col-lg-6 col-md-12 p-0 mx-auto position-relative border">
                        <div 
                          className="d-flex w-100 h-100 thmbnail-hover position-absolute align-items-center" 
                          style={style}>
                            <a 
                              className="btn btn-secondary border rounded mx-auto" 
                              href={p.appLink}
                              target="_blank"
                              rel="noreferrer">
                              <i className="bi bi-chevron-right fs-3"/>
                              <label>Visit Website</label>
                            </a>
                            <a 
                              className="btn btn-secondary border rounded mx-auto" 
                              href={p.codeLink}
                              target="_blank"
                              rel="noreferrer">
                              <i className="bi bi-code-slash fs-3"/>
                              See Code
                            </a>
                        </div>
                        <img src={p.thumbnail} className="img-fluid" alt={p.name + " thumbnail"}/>
                      </div>
                    }
                  </Motion>
                  <div className="col-12 col-md position-relative">
                    <h3>{p.name}</h3>
                    <p>{p.description}</p>
                  </div>
                </article>
              )
                }
          </div>
      </section>
    )
  }
