import React, { useRef } from "react";
import { useState } from "react";
import emailjs from "emailjs-com";

export const Contact = props => {
    const form = useRef();
    const [toSend, setToSend] = useState({ from_name: '', to_name: '', message: '', reply_to: '' });
  
    const onSubmit = e => {
      e.preventDefault();
      emailjs.send(
        'service_osecgz9',
        'template_hugptro',
        toSend,
        'user_LJGGYMoZAiXX9QAcZO4cw'
      )
        .then(response => window.alert("Mail sent"))
        .catch(err => window.alert("Failed to send mail"));
    };
  
    const handleChange = e => setToSend({ ...toSend, [e.target.name]: e.target.value });
    return (
      <section 
        id="section-5" 
        className="row min-vh-100 align-items-center">
        <div className="col w-75 my-5 py-3">
          <h2>Contact Me</h2>
          <form ref={form} style={{maxWidth: "500px"}} className="col mx-auto p-5 border rounded-0 shadow" onSubmit={onSubmit}>
            <input
              type='text'
              className="form-control rounded-0 my-1"
              name='from_name'
              placeholder='Name'
              value={toSend.from_name}
              onChange={handleChange}
              maxLength="100"
              required/>
            <input
              type='text'
              className="form-control rounded-0 my-1"
              name='subject'
              placeholder='Subject'
              value={toSend.subject}
              onChange={handleChange}
              maxLength="200"
              required/>
            <input
              type='email'
              className="form-control rounded-0 my-1"
              name='reply_to'
              placeholder='Email'
              value={toSend.reply_to}
              onChange={handleChange}
              maxLength="200"
              required/>
            <textarea
              className="form-control rounded-0 my-1"
              name='message'
              placeholder='Message'
              value={toSend.message}
              onChange={handleChange}
              required/>
            <button type='submit' className="btn btn-primary rounded-0 mx-auto px-3 py-2">Submit</button>
          </form>
        </div>
      </section>
    )
  }
